import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid'
import { stepCompleted, taskCompleted } from "../redux/actions"
import { useDispatch, useSelector } from "react-redux"

import "../App.css"



const TodoSubList = ({ propsParent, props }) => {
    const dispatch = useDispatch()
    console.log("props", props)


    const taskCompletion = (parent, child, completedChild) => {
        dispatch(stepCompleted(parent, completedChild))
        let index = 0, counter = 0, result = 0
        Object.keys(child).map(input => {
            //index counts the number of steps, while counter the number of completed steps
            index = index + 1
            if (child[input].completed > 0) {
                counter = (counter + 1)
                console.log("result for counter", counter)

            }
        })
        if (counter > 0) {
            result = (counter / index)
            console.log("result for new state", result)
            dispatch(taskCompleted(parent, result))
        }
        console.log("result for index", index)

        console.log("result for new state", result)
    }



    if (Object.keys(props).length >= 1) {
        let container =
            Object.keys(props).map(input =>

                <div className='container'>
                    {console.log("propsStep", props[input].step)}

                    <div className="subitem1-container ">
                        {props[input].step}
                    </div>
                    <div className="subitem1-container ">
                        Date
                </div>
                    <div className="subitem1-container ">
                        <div className="filler" style={{ width: `${props[input].completed * 100}%` }}>
                        </div>
                    </div>

                    <div className="subitem1-container ">
                        <button type="submit" onClick={() => dispatch(stepCompleted(propsParent, props[input]))}>Completed</button>
                        <button tpe="submit" onClick={() => taskCompletion(propsParent, props, props[input])}>refresh task</button>
                    </div>

                </div>
            )

        console.log("returning")
        return container
    }
    return null
}



export default TodoSubList;
