import { ADD_TODO, SHOW, COMPLETED, ADD_TODO_SLAVE, STEP_COMPLETED } from "./actionTypes"

export const addTodo = content => ({
    type: ADD_TODO,
    payload: {
        task: content,
        dateB: "1/1/1",
        dateE: "1/1/1",
        completed: 0,
        show: false,
        steps: {
        },

    }
})
//addTodo slave to create steps from addTodo
export const addTodoSlave = (content, contentSteps) => ({
    type: ADD_TODO_SLAVE,
    payload: {
        task: content,
        dateB: "1/1/1",
        dateE: "1/1/1",
        completed: 0,
        show: false,
        steps: contentSteps
    }
})

export const toogleShow = item => ({
    type: SHOW,
    payload: item
})

export const stepCompleted = (item, itemStep) => ({
    type: STEP_COMPLETED,
    payload: item,
    stepFinder: itemStep //temp properity folder for the substep index
})

export const taskCompleted = (item, newCompleted) => ({
    type: COMPLETED,
    payload: item,
    newComp: newCompleted
})
