import { ADD_TODO, SHOW, ADD_TODO_SLAVE, STEP_COMPLETED } from "../actionTypes"

const initialState = {

}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TODO: {
            const { task } = action.payload;
            return {

                ...state,
                [task]: {
                    ...action.payload,
                    task: task
                }

            }
        }

        case ADD_TODO_SLAVE: {
            const { task, steps } = action.payload;
            return {
                ...state,
                [task]: {
                    ...action.payload,
                    task: task,
                    steps: steps
                }
            }
        }

        case SHOW: {
            const { task, show } = action.payload
            return {
                ...state,
                [task]: {
                    ...action.payload,
                    show: !show
                }

            }
        }

        case STEP_COMPLETED: {
            const { task, steps } = action.payload
            const { step } = action.payload.steps
            return {
                ...state,
                [task]: {
                    ...action.payload,
                    steps: {
                        ...steps, [step]: { ...step, completed: 1 },
                    }
                }
            }
        }

        default: return state
    }
}

export default reducer
