import { ADD_TODO, SHOW, COMPLETED, ADD_TODO_SLAVE } from "./actionTypes"

export const addTodo = content => ({
    type: ADD_TODO,
    payload: {
        task: content,
        dateB: "1/1/1",
        dateE: "1/1/1",
        completed: false,
        show: false,
        steps: {
        },

    }
})
//addTodo slave to create steps from addTodo
export const addTodoSlave = (content, contentSteps) => ({
    type: ADD_TODO_SLAVE,
    payload: {
        task: content,
        dateB: "1/1/1",
        dateE: "1/1/1",
        completed: false,
        show: false,
        steps: contentSteps
    }
})

export const toogleShow = item => ({
    type: SHOW,
    payload: {
        task: item,
        dateB: "1/1/1",
        dateE: "1/1/1",
        completed: false,
        show: true,
        steps: [{
            miniT: "mini",
            completed: false
        },
        {
            miniT: 'test2'
        }]
    }
})
