import { ADD_TODO, SHOW } from "../actionTypes"

const initialState = {
    byTask: {}
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TODO: {
            const { idTask } = action.payload;
            return {
                ...state,
                byTask: {
                    ...state.byTask,
                    [idTask]: {
                        ...action.payload,
                        task: idTask
                    }
                }
            }
        }

        case SHOW: {
            const { idTask } = action.payload
            return {
                ...state,
                byTask: {
                    ...state.byTask,
                    [idTask]: {
                        show: !state.byTask[idTask].show
                    }
                }
            }
        }

        default: return state
    }
}

export default reducer
