import React, { Component, useState } from 'react';
import TodoSubList from './todosublist'
import ShowListItem from './showListItems'
import CreateTask from './createTask'
import { useDispatch, useSelector } from "react-redux"
import { toogleShow } from "../redux/actions"
import GetDataFromAPI from "./getDataAPI"
import axios from "axios"
import "../App.css"
import CreateCollection from './createCollection';

const createList = (e) => {

}
const fetchLists = () => { }

const TodoList = (props) => {
    const [list, setList] = useState("")
    const [modal, setModal] = useState(false)
    const [data, setData] = useState(props.data)
    const [category, setCategory] = useState("")
    console.log("data from APP.JS", props.data)

    //change category
    const changeCategory = (input) => {
        setCategory(input)
        console.log("cate4gori", category)
    }
    //setModal
    const toogleModal = () => {
        setModal(!modal);
        console.log("modal", modal)
    }
    const changeList = (e) => {
        setList(e.target.value);
        console.log("changeList", list)

    }
    //set up state from app.js
    let content = (
        <div>
            <div className="main">
                <div className="main-menu">
                    <div className="menu-boxes">
                        {Object.keys(data).map(input =>
                            <div className="btn-box">
                                <button onClick={() => changeCategory(data[input])} >{data[input]}</button>
                            </div>
                        )}

                    </div>
                    <div className="footer">
                        <div className="footer-box">
                            <button type="submit" onClick={() => toogleModal()}>Add Task</button>
                        </div>
                    </div>

                </div>
                <div className="main-content">
                    {
                        //if and else whether there is at least a category in the db
                        (data == null && modal) ?
                            <CreateTask
                                content={
                                    <input value={list} onChange={(e) => changeList(e)}></input>
                                }
                                closeModal={toogleModal}
                                category={category}>

                            </CreateTask> : <div></div>
                    }
                    {modal && <CreateTask
                        content={
                            <input value={list} onChange={(e) => changeList(e)}></input>
                        }
                        closeModal={toogleModal}
                        category={category}
                    />}

                    <ShowListItem
                    />

                </div>

            </div>


        </div>
    )
    return content
}


export default TodoList;
