import { Component, useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"
import { addTodo, addTodoSlave } from "../redux/actions"

const CreateTask = () => {
    const [task, setTask] = useState(null)
    const [emptyInputs, setEmptyInputs] = useState(["empty"])
    const [subT, setSubT] = useState("")
    const [subList, setSubList] = useState(null)
    const [butState, setButState] = useState(false)
    const dispatch = useDispatch()



    const formTask = (e) => {
        console.log("target", e.target.value)
        setTask(e.target.value)
        console.log("target", task)

    }


    const formSubt = (e) => {
        setSubT(e.target.value)
        console.log("subT", subT)
    }
    const addSubTask = () => {
        if (subT !== null) {
            if (subList == null) {
                setSubList({ [subT]: subT })
                console.log("if addSubTask", subList)
                setSubT("")
                console.log("ifsubt", subT)

            }

            else {
                console.log("else addSubTask", subList)
                setSubList({ ...subList, [subT]: subT })
                console.log("subList", subList)
                setSubT("")
            }
        }
        else return


    }
    //Set state for task and subList to null
    const dispatchAddTodoSlave = () => {
        dispatch(addTodoSlave(task, subList))
        setTask("");
        setSubList("");
        setButState(false)
    }
    const butSet = () => {
        setButState(!butState)

    }

    const deleteSubTask = (input) => {
        console.log("deleting step")
        let newSubList = Object.keys(subList).filter(helper => helper !== input)
        setSubList(newSubList)
        console.log("newSubList", subList)
    }

    let container = (<div>
        {!butState ? <div>
            <input value={task} onChange={(e) => formTask(e)}></input>
            <button type='submit' onClick={() => dispatch(addTodo(task))}>Add Task</button>
            <button type="submit" onClick={() => butSet()}>CreateSubTask</button>
        </div>
            :
            <div>
                <input value={task} onChange={(e) => formTask(e)}></input>
                <button type='submit' onClick={() => dispatchAddTodoSlave()}>Add Task</button>
                <button type="submit" onClick={() => butSet()}>DeleteAllSubTask</button>
                {subList === null ?
                    <div>
                        {console.log("inside true sublist")}
                        <input value={subT} onChange={(e) => formSubt(e)}></input>
                        <button type="submit" onClick={() => addSubTask()}>submit step</button>
                    </div>
                    :
                    <div>
                        {Object.keys(subList).map(input => <div>
                            <input value={subList[input]}></input>
                            <button type="submit" onClick={() => deleteSubTask(input)}>Delete Step</button>
                        </div>)}
                        <input value={subT} onChange={(e) => formSubt(e)}></input>
                        <button type="submit" onClick={() => addSubTask()}>submit step</button>
                    </div>
                }
            </div>}


        <br></br>

    </div >)

    return container


}

export default CreateTask
