import { ADD_TODO, SHOW, COMPLETED } from "./actionTypes"

export const addTodo = content => ({
    type: ADD_TODO,
    payload: {
        idTask: content,
        task: content,
        dateB: "1/1/1",
        dateE: "1/1/1",
        completed: false,
        show: false,
        steps: [{
            miniT: content.mini,
            completed: false
        },
        {
            miniT: 'test2'
        }]
    }
})

export const toogleShow = item => ({
    type: SHOW,
    payload: { item }
})
