import React, { Component, useState } from 'react';
import TodoSubList from './todosublist'
import "../App.css"


const TodoList = (props) => {
    //set up state from app.js
    const result = props.map(input => ({ [input.task]: false }))
    const convert = Object.fromEntries(result)
    console.log('convert', convert)
    const [butState, setBut] = useState(convert)
    console.log('sate', butState)

    //Lets return a div with ul with what is inside the state
    function setter(input) {
        setBut({ [input]: false })
        console.log('setter', butState)

    }
    function showSteps(input) {
        // setter(input)
        console.log('before click', butState)
        console.log('input', butState.input)
        let anot = (!butState.input) ? true : false;
        console.log("result", anot)
        setBut({ ...butState }, { [input]: anot })
        console.log('after click', butState)

    }
    let mapper =

        props.map(input =>
            <div className="holder">
                <div className="container">
                    <div className="item2">{input.task}<button type='button' onClick={() => showSteps([input.task])}>Clickhere</button></div>
                    <div className="item2">{input.dateE}</div>
                    <div className="item2">{input.dateB}</div>
                    <div className="item2">{input.completed}</div>

                </div>
                <div>{(input.steps.length > 0 && butState.input) && TodoSubList(input.steps)}
                </div>
            </div>
        )





    let content = (
        <div>
            <ul>
                {mapper}
            </ul>
        </div>
    )
    return content
}


export default TodoList;
