import { Component, useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { addTodo } from "../redux/actions"

const CreateTask = () => {
    const [task, setTask] = useState(null)
    const [emptyInputs, setEmptyInputs] = useState([])
    const [subT, setSubT] = useState(null)
    const [butState, setButState] = useState(false)
    const dispatch = useDispatch()

    const formTask = (e) => {
        console.log("target", e.target.value)
        setTask(e.target.value)
        console.log("target", task)

    }

    const addInput = () => {
        console.log("inside add", emptyInputs)
        setEmptyInputs([...emptyInputs, "empty"])
        console.log("inside add", emptyInputs)
    }

    const formSubt = (e) => {
        setSubT(e.target.value)
        console.log("subT", subT)
    }

    let container = (<div>
        <input value={task} onChange={(e) => formTask(e)}></input>
        <button type='submit' onClick={() => addInput()}>AddStpes</button>
        <button type='submit' onClick={() => dispatch(addTodo(task))}>Add Task</button>
        {emptyInputs !== null && <div>{emptyInputs.map(
            input => <input value={subT} onChange={(e) => formSubt(e)}></input>)}</div>

        }

        <br></br>

    </div >)

    return container


}

export default CreateTask
